mapper.select:
select /* CodeDao.getGrpCdList */ grp_cd as grp_cd /* varchar-그룹_코드 */ , grp_nm as grp_nm /* varchar-그룹_명 */ , prnt_grp_cd as prnt_grp_cd /* varchar-상위_그룹_코드 */ , use_yn as use_yn /* bpchar-사용_여부 */ , note as note /* text-비고 */ , reg_dt as reg_dt /* timestamp-등록_일시 */ , chg_dt as chg_dt /* timestamp-수정_일시 */ from comm_grp_cd /* 공통_그룹_코드 */ where 1=1
and grp_cd = #{grp_cd}
and grp_nm = #{grp_nm}
and prnt_grp_cd = #{prnt_grp_cd}
and use_yn = 'Y' /* bpchar-사용_여부 */

select /* CodeDao.getCdList */ grp_cd as grp_cd /* varchar-그룹_코드 */ , cd as cd /* varchar-코드 */ , cd_nm as cd_nm /* varchar-코드_명 */ , cd_v as cd_v /* varchar-코드_값 */ , note as note /* text-비고 */ , sq as sq /* numeric-순번 */ , use_yn as use_yn /* bpchar-사용_여부 */ , reg_dt as reg_dt /* timestamp-등록_일시 */ , chg_dt as chg_dt /* timestamp-수정_일시 */ from comm_cd /* 공통_코드 */ where 1=1
and grp_cd = #{grp_cd}
and cd = #{cd}
and cd_nm = #{cd_nm}
and cd_v = #{cd_v}
and note = #{note}
and sq = cast(#{sq} as numeric)
and use_yn = 'Y'
